/*globals tinymce */

function SGMathEditorPlugin(editor,url) {
    // Add a button that opens a window
    editor.addButton('mathblock', {
        text: 'math',
        icon: false,
        onclick: function() {
          editor.insertContent("\\( \\)");
        }
    });
    editor.addButton('sqrt', {
        text: '√',
        icon: false,
        onclick: function() {
          editor.insertContent("\\sqrt[2]{y}");
        }
    });
    editor.addButton('prod', {
        text: '∏',
        icon: false,
        onclick: function() {
          editor.insertContent("\\prod_{k=1}^n");
        }
    });
    editor.addButton('sum', {
        text: '∑',
        icon: false,
        onclick: function() {
          editor.insertContent("\\sum_{k=1}^n");
        }
    });
    editor.addButton('fraction', {
        text: '⅓',
        icon: false,
        onclick: function() {
          editor.insertContent("\\frac{1}{3} ");
        }
    });
}

tinymce.PluginManager.add('maths', SGMathEditorPlugin);

